00172 static int get_exts(void) {
00173 #ifdef _GLAD_IS_SOME_NEW_VERSION
00174     if(max_loaded_major < 3) {
00175 #endif
00176         exts = (const char *)glGetString(GL_EXTENSIONS);
00177 #ifdef _GLAD_IS_SOME_NEW_VERSION
00178     } else {
00179         unsigned int index;
00180 
00181         num_exts_i = 0;
00182         glGetIntegerv(GL_NUM_EXTENSIONS, &num_exts_i);
00183         if (num_exts_i > 0) {
00184             exts_i = (char **)malloc((size_t)num_exts_i * (sizeof *exts_i));
00185         }
00186 
00187         if (exts_i == NULL) {
00188             return 0;
00189         }
00190 
00191         for(index = 0; index < (unsigned)num_exts_i; index++) {
00192             const char *gl_str_tmp = (const char*)glGetStringi(GL_EXTENSIONS, index);
00193             size_t len = strlen(gl_str_tmp);
00194 
00195             char *local_str = (char*)malloc((len+1) * sizeof(char));
00196             if(local_str != NULL) {
00197                 memcpy(local_str, gl_str_tmp, (len+1) * sizeof(char));
00198             }
00199             exts_i[index] = local_str;
00200         }
00201     }
00202 #endif
