00217 static int has_ext(const char *ext) {
00218 #ifdef _GLAD_IS_SOME_NEW_VERSION
00219     if(max_loaded_major < 3) {
00220 #endif
00221         const char *extensions;
00222         const char *loc;
00223         const char *terminator;
00224         extensions = exts;
00225         if(extensions == NULL || ext == NULL) {
00226             return 0;
00227         }
00228 
00229         while(1) {
00230             loc = strstr(extensions, ext);
00231             if(loc == NULL) {
00232                 return 0;
00233             }
00234 
00235             terminator = loc + strlen(ext);
00236             if((loc == extensions || *(loc - 1) == ' ') &&
00237                 (*terminator == ' ' || *terminator == '\0')) {
00238                 return 1;
00239             }
00240             extensions = terminator;
00241         }
00242 #ifdef _GLAD_IS_SOME_NEW_VERSION
00243     } else {
00244         int index;
00245         if(exts_i == NULL) return 0;
00246         for(index = 0; index < num_exts_i; index++) {
00247             const char *e = exts_i[index];
00248 
00249             if(exts_i[index] != NULL && strcmp(e, ext) == 0) {
00250                 return 1;
00251             }
00252         }
00253     }
00254 #endif
00255 
00256     return 0;
00257 }
