02408     glad_glReadnPixels = (PFNGLREADNPIXELSPROC)load("glReadnPixels");
02409     glad_glGetnMapdv = (PFNGLGETNMAPDVPROC)load("glGetnMapdv");
02410     glad_glGetnMapfv = (PFNGLGETNMAPFVPROC)load("glGetnMapfv");
02411     glad_glGetnMapiv = (PFNGLGETNMAPIVPROC)load("glGetnMapiv");
02412     glad_glGetnPixelMapfv = (PFNGLGETNPIXELMAPFVPROC)load("glGetnPixelMapfv");
02413     glad_glGetnPixelMapuiv = (PFNGLGETNPIXELMAPUIVPROC)load("glGetnPixelMapuiv");
02414     glad_glGetnPixelMapusv = (PFNGLGETNPIXELMAPUSVPROC)load("glGetnPixelMapusv");
02415     glad_glGetnPolygonStipple = (PFNGLGETNPOLYGONSTIPPLEPROC)load("glGetnPolygonStipple");
02416     glad_glGetnColorTable = (PFNGLGETNCOLORTABLEPROC)load("glGetnColorTable");
02417     glad_glGetnConvolutionFilter = (PFNGLGETNCONVOLUTIONFILTERPROC)load("glGetnConvolutionFilter");
02418     glad_glGetnSeparableFilter = (PFNGLGETNSEPARABLEFILTERPROC)load("glGetnSeparableFilter");
02419     glad_glGetnHistogram = (PFNGLGETNHISTOGRAMPROC)load("glGetnHistogram");
02420     glad_glGetnMinmax = (PFNGLGETNMINMAXPROC)load("glGetnMinmax");
02421     glad_glTextureBarrier = (PFNGLTEXTUREBARRIERPROC)load("glTextureBarrier");
02422 }
02423 static int find_extensionsGL(void) {
02424     if (!get_exts()) return 0;
02425     (void)&has_ext;
02426     free_exts();
02427     return 1;
02428 }
02429 
02430 static void find_coreGL(void) {
02431 
02432     /* Thank you @elmindreda
02433      * https://github.com/elmindreda/greg/blob/master/templates/greg.c.in#L176
02434      * https://github.com/glfw/glfw/blob/master/src/context.c#L36
02435      */
02436     int i, major, minor;
02437 
02438     const char* version;
02439     const char* prefixes[] = {
02440         "OpenGL ES-CM ",
02441         "OpenGL ES-CL ",
02442         "OpenGL ES ",
02443         NULL
02444     };
02445 
02446     version = (const char*) glGetString(GL_VERSION);
02447     if (!version) return;
02448 
02449     for (i = 0;  prefixes[i];  i++) {
02450         const size_t length = strlen(prefixes[i]);
02451         if (strncmp(version, prefixes[i], length) == 0) {
02452             version += length;
02453             break;
02454         }
02455     }
02456 
02457 /* PR #18 */
02458 #ifdef _MSC_VER
02459     sscanf_s(version, "%d.%d", &major, &minor);
02460 #else
02461     sscanf(version, "%d.%d", &major, &minor);
02462 #endif
02463 
02464     GLVersion.major = major; GLVersion.minor = minor;
02465     max_loaded_major = major; max_loaded_minor = minor;
02466     GLAD_GL_VERSION_1_0 = (major == 1 && minor >= 0) || major > 1;
02467     GLAD_GL_VERSION_1_1 = (major == 1 && minor >= 1) || major > 1;
02468     GLAD_GL_VERSION_1_2 = (major == 1 && minor >= 2) || major > 1;
02469     GLAD_GL_VERSION_1_3 = (major == 1 && minor >= 3) || major > 1;
02470     GLAD_GL_VERSION_1_4 = (major == 1 && minor >= 4) || major > 1;
02471     GLAD_GL_VERSION_1_5 = (major == 1 && minor >= 5) || major > 1;
02472     GLAD_GL_VERSION_2_0 = (major == 2 && minor >= 0) || major > 2;
02473     GLAD_GL_VERSION_2_1 = (major == 2 && minor >= 1) || major > 2;
02474     GLAD_GL_VERSION_3_0 = (major == 3 && minor >= 0) || major > 3;
02475     GLAD_GL_VERSION_3_1 = (major == 3 && minor >= 1) || major > 3;
02476     GLAD_GL_VERSION_3_2 = (major == 3 && minor >= 2) || major > 3;
02477     GLAD_GL_VERSION_3_3 = (major == 3 && minor >= 3) || major > 3;
02478     GLAD_GL_VERSION_4_0 = (major == 4 && minor >= 0) || major > 4;
02479     GLAD_GL_VERSION_4_1 = (major == 4 && minor >= 1) || major > 4;
02480     GLAD_GL_VERSION_4_2 = (major == 4 && minor >= 2) || major > 4;
02481     GLAD_GL_VERSION_4_3 = (major == 4 && minor >= 3) || major > 4;
02482     GLAD_GL_VERSION_4_4 = (major == 4 && minor >= 4) || major > 4;
02483     GLAD_GL_VERSION_4_5 = (major == 4 && minor >= 5) || major > 4;
02484     if (GLVersion.major > 4 || (GLVersion.major >= 4 && GLVersion.minor >= 5)) {
02485         max_loaded_major = 4;
02486         max_loaded_minor = 5;
02487     }
02488 }
02489 
02490 int gladLoadGLLoader(GLADloadproc load) {
02491     GLVersion.major = 0; GLVersion.minor = 0;
02492     glGetString = (PFNGLGETSTRINGPROC)load("glGetString");
02493     if(glGetString == NULL) return 0;
02494     if(glGetString(GL_VERSION) == NULL) return 0;
02495     find_coreGL();
02496     load_GL_VERSION_1_0(load);
02497     load_GL_VERSION_1_1(load);
02498     load_GL_VERSION_1_2(load);
02499     load_GL_VERSION_1_3(load);
02500     load_GL_VERSION_1_4(load);
02501     load_GL_VERSION_1_5(load);
02502     load_GL_VERSION_2_0(load);
02503     load_GL_VERSION_2_1(load);
02504     load_GL_VERSION_3_0(load);
02505     load_GL_VERSION_3_1(load);
02506     load_GL_VERSION_3_2(load);
02507     load_GL_VERSION_3_3(load);
02508     load_GL_VERSION_4_0(load);
02509     load_GL_VERSION_4_1(load);
02510     load_GL_VERSION_4_2(load);
02511     load_GL_VERSION_4_3(load);
02512     load_GL_VERSION_4_4(load);
02513     load_GL_VERSION_4_5(load);
02514 
02515     if (!find_extensionsGL()) return 0;
02516     return GLVersion.major != 0 || GLVersion.minor != 0;
02517 }
